import React, { useEffect, useState } from 'react';
import "./CartPage.scss";
import { useSelector, useDispatch } from 'react-redux';
import { Link } from "react-router-dom";
import { removeFromCart, toggleCartQty, getCartTotal, clearCart } from '../../store/cartSlice';
import { formatPrice } from "../../utils/helpers";
import Pagination from '../../components/Pagination/Pagination'; // Import your Pagination component here

const CartPage = () => {
  const dispatch = useDispatch();
  const { data: cartProducts, totalItems, totalAmount, deliveryCharge } = useSelector(state => state.cart);

  useEffect(() => {
    dispatch(getCartTotal());
  }, [dispatch]);

  const emptyCartMsg = <h4 className='text-red fw-6'>No items found!</h4>;

  const [currentPage, setCurrentPage] = useState(1);
  const cartItemsPerPage = 5; // Number of cart items to display per page

  const startIndex = (currentPage - 1) * cartItemsPerPage;
  const endIndex = startIndex + cartItemsPerPage;
  const displayedCartItems = cartProducts.slice(startIndex, endIndex);

  return (
    <div className="cart-page">
      {/* ... Rest of your cart page JSX ... */}

      <div className='bg-ghost-white py-5'>
        <div className='container'>
          <div className='section-title bg-ghost-white'>
            {/* ... Rest of your section title JSX ... */}
          </div>
          {cartProducts.length === 0 ? (
            emptyCartMsg
          ) : (
            <div className="cart-content grid">
              <div className='cart-left'>
                <div className="cart-items grid">
                  {displayedCartItems.map((cartProduct) => (
                    <div className='cart-item grid' key={cartProduct.id}>
                      {/* ... Existing cart item rendering code ... */}
                    </div>
                  ))}
                </div>
                {/* ... Existing clear cart button ... */}

                {/* Pagination component */}
                <Pagination
                  currentPage={currentPage}
                  totalPages={Math.ceil(cartProducts.length / cartItemsPerPage)}
                  onPageChange={setCurrentPage}
                />
              </div>
              {/* ... Remaining cart summary ... */}
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default CartPage;
