import React, { useEffect, useState } from 'react';
import Slider from '../../components/Slider/Slider';
import Category from '../../components/Category/Category';
import SingleCategory from '../../components/SingleCategory/SingleCategory';
import Pagination from '../../components/Pagination/Pagination';
import { useSelector, useDispatch } from 'react-redux';
import { fetchCategories } from '../../store/categorySlice';
import { fetchProducts } from '../../store/productSlice';
import "./HomePage.scss";

const HomePage = () => {
  const dispatch = useDispatch();
  const { data: categories, status: categoryStatus } = useSelector((state) => state.category);
  const { data: allProducts, status: productStatus } = useSelector((state) => state.product);

  useEffect(() => {
    dispatch(fetchCategories());
    dispatch(fetchProducts());
  }, [dispatch]);

  return (
    <div className="home-page">
      <Slider />
      <Category categories={categories} status={categoryStatus} />
      
      {/* Display all products */}
      <section>
        <h2>All Products</h2>
        <SingleCategory products={allProducts} status={productStatus} />
        {/* You can add pagination for all products here if needed */}
      </section>
      
      {/* Display categories */}
      {categories.map((category, index) => (
        <section key={index}>
          <h2>{category.name}</h2>
          <SingleCategory products={category.catProductSingle} status={category.catProductSingleStatus} />
          <Pagination
            currentPage={category.catProductSinglePage}
            totalPages={category.catProductSingleTotalPages}
            onPageChange={(newPage) => dispatch(fetchProductsByCategory(index, 'single', newPage))}
          />
        </section>
      ))}
    </div>
  );
}

export default HomePage;
