import React, { useEffect, useState } from 'react';
import Slider from '../../components/Slider/Slider';
import Category from '../../components/Category/Category';
import ProductList from '../../components/ProductList/ProductList'; // Import your ProductList component here
import Pagination from '../../components/Pagination/Pagination';
import { useSelector, useDispatch } from 'react-redux';
import { fetchCategories } from '../../store/categorySlice';
import { fetchProducts } from '../../store/productSlice';
import "./HomePage.scss";

const HomePage = () => {
  const dispatch = useDispatch();
  const { data: categories, status: categoryStatus } = useSelector((state) => state.category);
  const { data: products, status: productStatus } = useSelector((state) => state.product);

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 25;
  console.log(products.length);
  const totalPages = Math.ceil(products.length / itemsPerPage); // Calculate the total number of pages

  useEffect(() => {
    dispatch(fetchCategories());
    dispatch(fetchProducts(currentPage, itemsPerPage));
  }, [dispatch, currentPage]);

  return (
    <div className="home-page">
      <Slider />
      <Category categories={categories} status={categoryStatus} />
      <ProductList products={products} status={productStatus} />

      {/* Pagination */}
      <Pagination
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={setCurrentPage}
      />
    </div>
  );
}

export default HomePage;
